// export default App;


class App extends React.Component {

  state = {
    user: this.props.user
  }

  setUser = user => {
    this.setState({
      user: user
    })
  }

  render() {
    return (
      <div className="App" >
          <Switch>
          <Route
        exact path="/spotdetails/:spotId"
        // component={SpotDetails}
        render={props => <SpotDetails user={this.state.user} {...props}/>}
        />
        <div>

        <Navbar 
          user={this.state.user} 
          setUser={this.setUser} 
        />


        {/* <Route hel
          exact
          path='/'
          // component={SpotList}
          render={props => {
            if (this.state.user) return <SpotList {...props} />
            else return <Redirect to='/' />
          }}
        /> */}


        <Route
          exact
          path='/signup'
          render={props => <Signup setUser={this.setUser} {...props} />}
        />

        <Route
          exact
          path='/profile'
          render={props => <Profile {...props} />}
        />

        <Route
          exact
          path='/'
          render={props => <Home {...props} />}
        />

        <Route
          exact
          path='/login'
          render={props => <Login setUser={this.setUser} {...props} />}
        />

        <Route 
        exact path='/list'
        component={SpotList}
        />


        <Route
        exact path="/addSpot"
        // component={AddSpot}
        render={props=> {
          if (this.state.user) return <AddSpot {...props} />
          else return <Redirect to="/signup"/>
        }}
        />

        <Route 
        exact path="/edit/:spotId"
        component={EditSpot}
        // render={props => <EditSpot user={this.state.user} {...props}/>}
        />

        <Route
        exact path="/profile"
        component={Profile}
        />


        <Route
          exact path="/profile"
          render={(props) => (
            <Profile
              setUser={this.setUser} user={this.state.user} 
              {...props}
            />
          )}
        />

        <Route 
        exact path="/map"
        component={Map}
        />

        </div>
      </Switch>
      <div className="footer">Â© IRONHACK WebDev Bootcamp Berlin 2020</div>
      </div>
    );
  }

}
export default App;

